import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import { useSelector, useDispatch } from 'react-redux';
import List from '@material-ui/core/List';

import { toggleSidebar } from '../../redux/actions';
import FavCountry from './FavCountry';
import { ThemeContext } from '../../App';

const useStyles = makeStyles({
    paper: {
        backgroundColor: "#333",
        minWidth: "300px",
        color: "white"
    },

    noFav: {
        textAlign: "center"
    }
});

export default function FavDrawer() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const isOpened = useSelector(store => store.toggleReducer.open)
    const favCountries = useSelector(store => store.countryReducer.favCountries)
    console.log(favCountries);

    const close = () => {
        dispatch(toggleSidebar());
    }

    return (
        <div>
            <React.Fragment>
                <Drawer
                    classes={{ paper: classes.paper }}
                    anchor="right"
                    open={isOpened}
                    onClose={close}>
                    <List>
                        {favCountries.length > 0
                            ? favCountries.map(fav => {
                                return <FavCountry key={fav.name} country={fav} />
                            })
                            : <span className={classes.noFav}>No favorite countries yet</span>}
                    </List>
                </Drawer>

            </React.Fragment>

        </div>
    );
}
