import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Badge from '@material-ui/core/Badge';
import Fab from '@material-ui/core/Fab';
import FavoriteIcon from '@material-ui/icons/Favorite';
import { useDispatch, useSelector } from 'react-redux';

import { toggleSidebar } from '../../redux/actions'
import SearchField from './SearchField';
import ChooseTheme from './ChooseTheme';

const useStyles = makeStyles((theme) => ({
    grow: {
        flexGrow: 1,
    },

    nav: {
        backgroundColor: "#121212"
    },

    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    favList: {
        backgroundColor: "#f48fb1"
    }
}));

export default function Navbar() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const favCountries = useSelector(store => store.countryReducer.favCountries);
    const openFav = () => {
        dispatch(toggleSidebar());
    }

    return (
        <div className={classes.grow}>
            <AppBar className={classes.nav} position="static">
                <Toolbar className={classes.toolbar}>
                    <div>
                        <Typography className={classes.title} variant="h6" noWrap>
                            Countries
                    </Typography>

                        <SearchField />
                    </div>

                    <ChooseTheme />

                    <Badge onClick={openFav} badgeContent={favCountries.length} overlap="circle" color="secondary" showZero>
                        <Fab size="small" className={classes.favList} aria-label="favorite countries">
                            <FavoriteIcon />
                        </Fab>
                    </Badge>

                </Toolbar>
            </AppBar>
        </div>
    );
}
