import { useEffect } from "react";

import { useDispatch, useSelector } from 'react-redux';
import { getCountrySaga } from '../redux/actions';

function useCountry(name) {
    //const [isLoading, setIsLoading] = useState(false);
    const dispatch = useDispatch();

    let allCountries = useSelector(store => store.countryReducer.country);
    let country = useSelector(store => store.countryReducer.allCountries);

    useEffect(() => {
        dispatch(getCountrySaga(name));
    }, [dispatch])

    return name ? country : allCountries;
}

export default useCountry;
