import { createContext, useState } from 'react';
import { Switch, Route } from 'react-router-dom';

import CountryTable from './components/table/CountryTable';
import Navbar from './components/navbar/Navbar'
import FavDrawer from './components/drawer/FavDrawer'

import './App.css';
import { darkTheme, lightTheme } from './themes';

const themes = { lightTheme, darkTheme };
export const ThemeContext = createContext(themes.darkTheme);

function App() {
  const [theme, setTheme] = useState(themes.darkTheme);
  const context = {
    currentTheme: theme
  }
  return (
    <ThemeContext.Provider value={theme} className="App">
      <Navbar />
      <Switch>
        <Route exact path="/">
          <CountryTable />
        </Route>

        {/* <Route exact path="/:countryName">
          <SingleCountry />
        </Route> */}

      </Switch>
      <FavDrawer />
    </ThemeContext.Provider>
  );
}

export default App;
