import { createContext, useState } from 'react';
import { Switch, Route } from 'react-router-dom';

import CountryTable from './components/table/CountryTable';
import Navbar from './components/navbar/Navbar'
import FavDrawer from './components/drawer/FavDrawer'

import './App.css';
import { darkTheme } from './themes';

export const ThemeContext = createContext();

function App() {
  //console.log();
  const [theme, setTheme] = useState(window.localStorage.getItem("theme") ||darkTheme);

  const context = {
    currentTheme: theme,
    switchTheme: setTheme
  }

  return (
    <ThemeContext.Provider value={context} className="App">
      <Navbar />
      <Switch>
        <Route exact path="/">
          <CountryTable />
        </Route>

        {/* <Route exact path="/:countryName">
          <SingleCountry />
        </Route> */}

      </Switch>
      <FavDrawer />
    </ThemeContext.Provider>
  );
}

export default App;
