import { TOGGLE_SIDEBAR } from '../actions';

const initialState = { sidebarDisplay: false };

const toggle = (state = initialState, action) => {
    switch (action.type) {
        case TOGGLE_SIDEBAR:
            return { ...state, sidebarDisplay: !state.sidebarDisplay };
        default:
            return state;
    }
}

export default toggle;
