import React, { useState, useContext } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';

import Paper from '@material-ui/core/Paper';
import TablePagination from '@material-ui/core/TablePagination';

//import CircularProgress from '@material-ui/core/CircularProgress';

import useCountry from '../../custom-hooks/useCountry';
import CountryTableRow from './CountryTableRow';
import CountryTableHead from './CountryTableHead';
import getComparator from '../../helperFunctions';
import { useDispatch, useSelector } from 'react-redux';
import { getCountrySuccess } from '../../redux/actions';

import {} from '../../'

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: "#333",
        padding: "50px"
    },

    container: {
        borderRadius: "1rem",
        override: "hidden",
        backgroundColor: "#424242",
    },

    table: {
        minWidth: 650,
        backgroundColor: "#424242",
    }
}));

function CountryTable(props) {
    const allCountries = useCountry();
    const dispatch = useDispatch();

    const [page, setPage] = useState(0);
    //Ascending or Descending
    const [order, setOrder] = useState("asc");
    //Order by name, population or region
    const [orderBy, setOrderBy] = useState("name");

    //const [isLoading, setIsLoading] = useState(false);
    const classes = useStyles();
    const rowsPerPage = 10;

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const sort = () => {
        const sortedCountries = [...allCountries].sort(getComparator(order, orderBy));
        dispatch(getCountrySuccess(sortedCountries));
    }

    return (
        <div className={classes.root}>
            <TableContainer className={classes.container} component={Paper}>
                <Table
                    className={classes.table}
                    aria-label="simple table">

                    <CountryTableHead
                        orderBy={orderBy}
                        setOrderBy={setOrderBy}
                        order={order}
                        setOrder={setOrder}
                        sort={sort} />

                    <TableBody>
                        {allCountries
                            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                            .map((country) => (
                                <CountryTableRow key={country.name} country={country} />
                            ))}
                    </TableBody>

                </Table>
            </TableContainer>

            <TablePagination
                component="div"
                count={allCountries.length}
                page={page}
                onChangePage={handleChangePage}
                rowsPerPage={rowsPerPage}
                rowsPerPageOptions={[5]}
            />

        </div >
    )
}


export default CountryTable;
