// Variables for tgoogle actions
const TOGGLE_SIDEBAR = "TOGGLE_SIDEBAR";
const TOGGLE_HEART = "TOGGLE_HEART";

//Variables for favorite list related actions
const LIKE = "LIKE";
const UNLIKE = "DISLIKE";

// Switch the appearance of the sidebar
const toggleSidebar = () => {
    return {
        type: TOGGLE_SIDEBAR
    }
}

// Switch the like of users (like and dislike countries)
const toggleHeart = () => {
    return {
        type: TOGGLE_HEART
    }
}

//Add and remove countries from favorite list
const likeCountry = (country, allCountries) => {
    return {
        type: LIKE,
        payload: { country, setCountries }
    }
}

const unlikeCountry = (countryName, allCountries) => {
    return {
        type: UNLIKE,
        payload: { countryName, allCountries }
    }
}

export {
    TOGGLE_SIDEBAR, TOGGLE_HEART, LIKE, UNLIKE,
    toggleSidebar, toggleHeart, likeCountry, unlikeCountry
};
