import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';

const headCells = [
    { id: 'flag', numeric: false, isSortable: false, label: 'Flag' },
    { id: 'name', numeric: false, isSortable: true, label: 'Name' },
    { id: 'population', numeric: true, isSortable: true, label: 'Population' },
    { id: 'region', numeric: false, isSortable: true, label: 'Region' },
    { id: 'languages', numeric: false, isSortable: false, label: 'Languages' }
];

const useStyles = makeStyles({

});

function CountryTableHead({ order, setOrder, orderBy, setOrderBy }) {
    const classes = useStyles();

    const sortBy = (id) => {
        return () => console.log(id);
        /*if (orderBy !== id) {
            return (id) => setOrderBy(id);
        } else {
            return () => setOrder(order === "asc" ? "desc" : "asc");
        }*/
    }

    return (
        <TableHead className={classes.header}>
            <TableRow>
                {headCells.map(cell => (
                    <TableCell
                        key={cell.id}
                        align="center">
                        <span onClick={sortBy(cell.id)}>{cell.label}</span>
                        {
                            cell.isSortable &&
                            <TableSortLabel
                                active={orderBy === cell.id}
                                direction={orderBy === cell.id ? order : 'asc'} />
                        }
                    </TableCell>
                ))}
            </TableRow>
        </TableHead >
    )
}

export default CountryTableHead;
