import { createContext, useState } from 'react';
import { Switch, Route } from 'react-router-dom';

import CountryTable from './components/table/CountryTable';
import FavDrawer from './components/drawer/FavDrawer';
import SingleCountry from './components/country/SingleCountry';

import { darkTheme } from './themes';
import './App.css';

type Context = [typeof darkTheme, React.Dispatch<any>]
export const ThemeContext: Context = createContext([darkTheme, () => { }]);

function App() {
  const [theme, setTheme] = useState(JSON.parse(window.localStorage.getItem("theme")) || darkTheme);

  const context = {
    currentTheme: theme,
    switchTheme: setTheme
  }

  return (
    <ThemeContext.Provider value={context} className="App">
      <Switch>
        <Route exact path="/">
          <CountryTable />
        </Route>

        <Route exact path="/:name">
          <SingleCountry />
        </Route>

      </Switch>
      <FavDrawer />
    </ThemeContext.Provider>

  );
}

export default App;
