import { createStore, combineReducers, applyMiddleware } from 'redux';
import { composeWithDevTools } from 'redux-devtools-extension';

import favCountriesReducer from './reducers/favCountriesReducer';
import toggleReducer from './reducers/toggleReducer';
import rootSaga from './saga/countrySaga'
const rootReducer = combineReducers({ favCountriesReducer, toggleReducer });

const initialState = {
    favCountriesReducer: {
        favCountries: []
    },
    toggleReducer: {
        open: false
    }
}
const store = createStore(rootReducer, initialState, composeWithDevTools(applyMiddleware()));

export default store;
