import React from 'react'
import { Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import TableRow from '@material-ui/core/TableRow';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';
import FavoriteIcon from '@material-ui/icons/Favorite';
import { useDispatch, useSelector } from 'react-redux';

import CountryTableCell from './CountryTableCell';
import { likeCountry } from '../../redux/actions'

const useStyles = makeStyles({
    flag: {
        width: "100px",
        height: "65px",
        borderRadius: "1rem"
    },

    like: {
        fontSize: "1.5rem",
        "&:hover": {
            cursor: "pointer",
            color: "pink"
        }
    },

    liked: {
        width: "2rem",
        color: "pink"
    }
});

function CountryTableRow({ country }) {
    const classes = useStyles();
    const { flag, name, region, population, languages } = country;

    const dispatch = useDispatch();
    const favCountries = useSelector(store => store.countryReducer.favCountries);

    console.log("hih");

    const like = () => {
        dispatch(likeCountry(country));
    }

    return (
        <TableRow className={classes.root}>
            <CountryTableCell>
                <img
                    src={flag}
                    alt={`${name}'s flag`}
                    className={classes.flag} />
            </CountryTableCell>
            <CountryTableCell align="center">
                <Link to={`/${name}`}>{name}</Link>
            </CountryTableCell>
            <CountryTableCell align="center">{population}</CountryTableCell>
            <CountryTableCell align="center">{region}</CountryTableCell>
            <CountryTableCell align="center">{languages.map(lang => lang.name).join(", ")}</CountryTableCell>
            <CountryTableCell align="center">
                {favCountries.find(country => country.name === name)
                    ? <FavoriteIcon className={classes.liked} />
                    : <FavoriteBorderIcon onClick={like} className={classes.like} />}
            </CountryTableCell>
        </TableRow>
    )
}


export default CountryTableRow;
