import { GET_COUNTRY_SUCCESS, LIKE, UNLIKE, SEARCH } from '../actions';

const initialState =
{
    favCountries: JSON.parse(window.localStorage.getItem("favCountries")) || [],
    allCountries: [],
    searchCountries: [],
    country: {},
    input: ""
};

const countryReducer = (state = initialState, action) => {
    switch (action.type) {
        case LIKE:
            const newFav = [...state.favCountries, action.payload.country];
            window.localStorage.setItem("favCountries", JSON.stringify(newFav));
            return { ...state, favCountries: newFav };
        case UNLIKE:
            const remainingFav = state.favCountries.filter(country => country.name !== action.payload.countryName);
            window.localStorage.setItem("favCountries", JSON.stringify(remainingFav));
            return { ...state, favCountries: remainingFav };
        case GET_COUNTRY_SUCCESS:
            const { country } = action.payload;
            if (country.length === 1) {
                return { ...state, country: country[0] };
            } else {
                return { ...state, allCountries: country, searchCountries: country };
            }
        case SEARCH:
            const input = action.payload.input;
            const searchResults = state.allCountries.filter(country => country.name.slice(0, input.length).toLowerCase() === input.toLowerCase());
            console.log(searchResults);
            return { ...state, searchCountries: searchResults, input: input }
        default:
            return state;
    }
}

export default countryReducer;
