import { GET_COUNTRY_SAGA, LIKE, UNLIKE } from '../actions';

const initialState = { favCountries: [], allCountries: [], country: {} };

const favCountriesReducer = (state = initialState, action) => {
    switch (action.type) {
        case LIKE:
            return { ...state, favCountries: [...state.favCountries, action.payload.country] };
        case UNLIKE:
            return { ...state, favCountries: state.favCountries.filter(country => country.name !== action.payload.countryName) };
        case GET_COUNTRY_SUCCESS:
            const country = action.payload.country;
            if (country.length === 1) {
                return { ...state, country: country };
            } else {
                return { ...state, allCountries: country }
            }

        default:
            return state;
    }
}

export default favCountriesReducer;
