import { GET_COUNTRY_SUCCESS, LIKE, UNLIKE, SEARCH } from '../actions';

const initialState = { favCountries: [], allCountries: [], country: {}, input: "" };

const countryReducer = (state = initialState, action) => {
    switch (action.type) {
        case LIKE:
            return { ...state, favCountries: [...state.favCountries, action.payload.country] };
        case UNLIKE:
            return { ...state, favCountries: state.favCountries.filter(country => country.name !== action.payload.countryName) };
        case GET_COUNTRY_SUCCESS:
            const country = action.payload.country;
            if (country.length === 1) {
                return { ...state, country: country[0] };
            } else {
                return { ...state, allCountries: country };
            }
        case SEARCH:
            const input = action.payload.input;
            const searchResults = state.allCountries.filter(country => country.name.slice(0, input.length).toLowerCase() === input.toLowerCase());
            return { ...state, allCountries: searchResults, input: input }
        default:
            return state;
    }
}

export default countryReducer;
