import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import TablePagination from '@material-ui/core/TablePagination';
//import CircularProgress from '@material-ui/core/CircularProgress';

import useCountry from '../custom-hooks/useCountry';

import CountryTableRow from './CountryTableRow';
import CountryTableCell from './CountryTableCell';

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: "#1F2D50",
        padding: "50px"
    },

    container: {
        borderRadius: "1rem",
        override: "hidden",
        backgroundColor: "#22345A",
    },

    table: {
        minWidth: 650,
        backgroundColor: "#22345A",
    }
}));

function CountryTable(props) {
    const [errorCountries, allCountries] = useCountry();
    const [page, setPage] = useState(0);
    const [isLoading, setIsLoading] = useState(false);
    const classes = useStyles();
    const rowsPerPage = 10;

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    return (
        <div className={classes.root}>
            <TableContainer className={classes.container} component={Paper}>
                <Table className={classes.table}
                    aria-label="simple table">

                    <TableHead className={classes.header}>
                        <TableRow>
                            <CountryTableCell align="center">Flag</CountryTableCell>
                            <CountryTableCell align="center">Name</CountryTableCell>
                            <CountryTableCell align="center">Population</CountryTableCell>
                            <CountryTableCell align="center">Region</CountryTableCell>
                            <CountryTableCell align="center">Languages</CountryTableCell>
                        </TableRow>
                    </TableHead>

                    <TableBody>
                        {allCountries
                            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                            .map((country) => (
                                <CountryTableRow key={country.name} country={country} />
                            ))}
                    </TableBody>
                </Table>
            </TableContainer>

            <TablePagination
                component="div"
                count={allCountries.length}
                page={page}
                onChangePage={handleChangePage}
                rowsPerPage={rowsPerPage}
            />

        </div>
    )
}


export default CountryTable;
