import React, { useContext } from 'react';
import Radio from '@material-ui/core/Radio';

import { ThemeContext } from '../../App';
import { darkTheme, lightTheme } from '../../themes';

function ChooseTheme() {
    const { currentTheme, switchTheme } = useContext(ThemeContext);
    const handleChange = () => {
        currentTheme.name === "dark"
            ? switchTheme(lightTheme)
            : switchTheme(darkTheme)
    }
    return (
        <div>
            <Radio
                checked={currentTheme.name === "dark"}
                onChange={handleChange}
                value="dark"
                name="theme"
                inputProps={{ 'aria-label': 'dark theme' }}
                color="default"
            />
            <Radio
                checked={currentTheme.name === "light"}
                onChange={handleChange}
                value="light"
                name="theme"
                inputProps={{ 'aria-label': 'light theme' }}
            />
        </div>
    )
}

export default ChooseTheme;
