import { GET_COUNTRY_SUCCESS, SEARCH_COUNTRY_SUCCESS, AllActions, REACT_COUNTRY_SUCCESS } from '../actions';
import { Country } from '../../types/types';

type CountryState = {
    favCountries: Country[];
    allCountries: Country[];
    country?: Country;
}

const initialState: CountryState =
{
    favCountries: localStorage.getItem("favCountries")
        ? JSON.parse(localStorage.getItem("favCountries") as string)
        : [],
    allCountries: [],
};

const countryReducer = (state = initialState, action: AllActions): CountryState => {
    switch (action.type) {
        case REACT_COUNTRY_SUCCESS:
            const newFav = action.payload;
            return { ...state, favCountries: newFav }


        case GET_COUNTRY_SUCCESS:
            const country = action.payload;
            if (country.length === 1) {
                return { ...state, country: country[0] };
            } else {
                return { ...state, allCountries: country };
            }

        case SEARCH_COUNTRY_SUCCESS:
            const countries = action.payload;

            return { ...state, allCountries: countries }
        default:
            return state;
    }
}

export default countryReducer;
