// your custom hook goes here
import { useEffect } from "react";

import { useDispatch, useSelector } from 'react-redux';
import { getCountrySaga } from '../redux/actions';

function useCountry(name) {
    const [isLoading, setIsLoading] = useState(false);
    const dispatch = useDispatch();

    let allCountries = useSelector(store => store.countryReducer.searchCountries);

    let country = useSelector(store => store.countryReducer.country);

    useEffect(() => {
        dispatch(getCountrySaga(name, setIsLoading));
    }, [dispatch, name])

    return name ? country : allCountries;
}

export default useCountry;
