import React from 'react';
import { fade, makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import Badge from '@material-ui/core/Badge';
import SearchIcon from '@material-ui/icons/Search';
import Fab from '@material-ui/core/Fab';
import FavoriteIcon from '@material-ui/icons/Favorite';
import { useDispatch, useSelector } from 'react-redux';

import { toggleSidebar } from '../../redux/actions'

const useStyles = makeStyles((theme) => ({
    grow: {
        flexGrow: 1,

    },

    nav: {
        backgroundColor: "#121212"
    },

    title: {
        display: 'none',
        [theme.breakpoints.up('sm')]: {
            display: 'block',
        },
    },
    search: {
        position: 'relative',
        borderRadius: theme.shape.borderRadius,
        backgroundColor: fade(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.white, 0.25),
        },
        marginRight: theme.spacing(2),
        marginLeft: 0,
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            marginLeft: theme.spacing(3),
            width: 'auto',
        },
    },
    searchIcon: {
        padding: theme.spacing(0, 2),
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('md')]: {
            width: '20ch',
        },
    },

    favList: {
        backgroundColor: "#f48fb1"
    }
}));

export default function Navbar() {
    const classes = useStyles();
    const dispatch = useDispatch();
    const favCountries = useSelector(store => store.favCountries.favCountries);
    const openFav = () => {
        dispatch(toggleSidebar());
    }

    return (
        <div className={classes.grow}>
            <AppBar className={classes.nav} position="static">
                <Toolbar>
                    <Typography className={classes.title} variant="h6" noWrap>
                        Countries
                    </Typography>

                    <div className={classes.search}>
                        <div className={classes.searchIcon}>
                            <SearchIcon />
                        </div>
                        <InputBase
                            placeholder="Search…"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>

                    <Badge onClick={openFav} badgeContent={favCountries.length} overlap="circle" color="secondary" showZero>
                        <Fab size="small" className={classes.favList} aria-label="favorite countries">
                            <FavoriteIcon />
                        </Fab>
                    </Badge>

                </Toolbar>
            </AppBar>
        </div>
    );
}
