import { LIKE, UNLIKE } from '../actions';

const initialState = { favCountries: [] };

const favCountries = (state = initialState, action) => {
    switch (action.type) {
        case LIKE:
            action.payload.setCountries()
            return { ...state, favCountries: [...state.favCountries, action.payload.country] };
        case UNLIKE:
            return { ...state, favCountries: state.favCountries.filter(country => country.name !== action.payload.countryName) };
        default:
            return state;
    }
}

export default favCountries;
