import { Country } from '../types/types';
import { ActionReturn } from '../types/reduxTypes';

// Variables for tgoogle actions
export const TOGGLE_SIDEBAR = "TOGGLE_SIDEBAR";

//Variables for favorite list related actions
export const LIKE = "LIKE";
export const UNLIKE = "DISLIKE";

//Variables for fetching countries data action
export const GET_COUNTRY_SAGA = "GET_COUNTRY_SAGA";
export const GET_COUNTRY_SUCCESS = "GET_COUNTRY_SUCCESS";

//Variables for searching
export const SEARCH = "UPDATE_SEARCH";

// Switch the appearance of the sidebar
export const toggleSidebar = (): ActionReturn<typeof TOGGLE_SIDEBAR, null> => {
    return {
        type: TOGGLE_SIDEBAR
    }
}

export const likeCountrySaga 
//Add and remove countries from favorite list
export const likeCountry = (country: Country): ActionReturn<typeof LIKE, Country> => {
    return {
        type: LIKE,
        payload: country
    }
}

export const unlikeCountry = (countryName: string): ActionReturn<typeof UNLIKE, string> => {
    return {
        type: UNLIKE,
        payload: countryName
    }
}

//Action to trigger the saga
export const getCountrySaga = (name: string | undefined): ActionReturn<typeof GET_COUNTRY_SAGA, string | undefined> => {
    return {
        type: GET_COUNTRY_SAGA,
        payload: name
    }
}

//Action to delever the data from saga to reducer
export const getCountrySuccess = (country: Country[]): ActionReturn<typeof GET_COUNTRY_SUCCESS, Country[]> => {
    return {
        type: GET_COUNTRY_SUCCESS,
        payload: country
    }
}

// Action for searching
export const search = (input: string): ActionReturn<typeof SEARCH, string> => {
    return {
        type: SEARCH,
        payload: input
    }
}

export type AllActions = ReturnType<typeof toggleSidebar
    | typeof likeCountry
    | typeof unlikeCountry
    | typeof getCountrySaga
    | typeof getCountrySuccess
    | typeof search>
