// Variables for tgoogle actions
const TOGGLE_SIDEBAR = "TOGGLE_SIDEBAR";

//Variables for favorite list related actions
const LIKE = "LIKE";
const UNLIKE = "DISLIKE";

// Switch the appearance of the sidebar
const toggleSidebar = () => {
    return {
        type: TOGGLE_SIDEBAR
    }
}

// Switch the like of users (like and dislike countries)
const toggleHeart = () => {
    return {
        type: TOGGLE_HEART
    }
}

//Add and remove countries from favorite list
const likeCountry = (country) => {
    return {
        type: LIKE,
        payload: { country }
    }
}

const unlikeCountry = (countryName) => {
    return {
        type: UNLIKE,
        payload: { countryName }
    }
}

export {
    TOGGLE_SIDEBAR, LIKE, UNLIKE,
    toggleSidebar, likeCountry, unlikeCountry
};
