import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';

import Paper from '@material-ui/core/Paper';
import TablePagination from '@material-ui/core/TablePagination';

//import CircularProgress from '@material-ui/core/CircularProgress';

import useCountry from '../custom-hooks/useCountry';

import CountryTableRow from './CountryTableRow';

import CountryTableHead from './CountryTableHead';

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: "#1F2D50",
        padding: "50px"
    },

    container: {
        borderRadius: "1rem",
        override: "hidden",
        backgroundColor: "#22345A",
    },

    table: {
        minWidth: 650,
        backgroundColor: "#22345A",
    }
}));

function CountryTable(props) {
    const [errorCountries, allCountries] = useCountry();
    const [page, setPage] = useState(0);

    //Ascending or Descending
    const [order, setOrder] = useState("asc");
    //Order by name, population or region
    const [orderBy, setOrderBy] = useState("name");

    //const [isLoading, setIsLoading] = useState(false);
    const classes = useStyles();
    const rowsPerPage = 10;

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    function descendingComparator(a, b, orderBy) {
        if (b[orderBy] < a[orderBy]) {
          return -1;
        }
        if (b[orderBy] > a[orderBy]) {
          return 1;
        }
        return 0;
      }

    const sort = () => {
        if (order === "asc") {
            const sortedCountries = [...allCountries].sorted(
                (countryA, countryB, orderBy) => {
                    country[orderBy]
                })
        }
    }

    return (
        <div className={classes.root}>
            <TableContainer className={classes.container} component={Paper}>
                <Table
                    className={classes.table}
                    aria-label="simple table">

                    <CountryTableHead
                        orderBy={orderBy}
                        setOrderBy={setOrderBy}
                        order={order}
                        setOrder={setOrder} />

                    <TableBody>
                        {allCountries
                            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                            .map((country) => (
                                <CountryTableRow key={country.name} country={country} />
                            ))}
                    </TableBody>

                </Table>
            </TableContainer>

            <TablePagination
                component="div"
                count={allCountries.length}
                page={page}
                onChangePage={handleChangePage}
                rowsPerPage={rowsPerPage}
            />

        </div >
    )
}


export default CountryTable;
