import React from 'react'
import { Link } from 'react-router-dom';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';

import CountryTableCell from './CountryTableCell'


const Country = withStyles((theme) => ({
    head: {
        backgroundColor: theme.palette.common.black,
        color: theme.palette.common.white,
    },
    body: {
        fontSize: 14,
    },
}))(TableCell);

const useStyles = makeStyles({
    root: {
        borderColor: "red",
    },

    flag: {
        width: "200px"
    }
});

function CountryTable(props) {
    const classes = useStyles();
    const { flag, name, region, population, languages } = props.country;

    return (
        <TableRow className={classes.root}>
            <Country>
                <img
                    src={flag}
                    alt={`${name}'s flag`}
                    className={classes.flag} />
            </Country>
            <Country align="center">
                <Link to={`/${name}`}>{name}</Link>
            </Country>
            <Country align="center">{population}</Country>
            <Country align="center">{region}</Country>
            <Country align="center">{languages.map(lang => lang.name).join(",")}</Country>
        </TableRow>
    )
}


export default CountryTable;
