import React from 'react'
import { Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import TableRow from '@material-ui/core/TableRow';
import FavoriteBorderIcon from '@material-ui/icons/FavoriteBorder';

import CountryTableCell from './CountryTableCell';

const useStyles = makeStyles({
    root: {
        borderColor: "red",
    },

    flag: {
        width: "200px",
        height: "120px"
    },

    like: {

    }
});

function CountryTable(props) {
    const classes = useStyles();
    const { flag, name, region, population, languages } = props.country;

    return (
        <TableRow className={classes.root}>
            <CountryTableCell>
                <img
                    src={flag}
                    alt={`${name}'s flag`}
                    className={classes.flag} />
            </CountryTableCell>
            <CountryTableCell align="center">
                <Link to={`/${name}`}>{name}</Link>
            </CountryTableCell>
            <CountryTableCell align="center">{population}</CountryTableCell>
            <CountryTableCell align="center">{region}</CountryTableCell>
            <CountryTableCell align="center">{languages.map(lang => lang.name).join(",")}</CountryTableCell>
            <CountryTableCell align="center"><FavoriteBorderIcon className={classes.like} /></CountryTableCell>
        </TableRow>
    )
}


export default CountryTable;
