import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from "@material-ui/core/Paper";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';

import useCountry from '../../custom-hooks/useCountry';
import { ThemeContext } from '../../App';

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: theme => theme.palette.background,
        height: "100%",
        overflowX: "hidden",
        padding: "100px"
    },

    paper: {
        backgroundColor: theme => theme.palette.table,
        padding: "50px",
        height: "100%",

        "& h1": {
            fontSize: "1.5rem"
        }
    },

    flag: {
        width: "100%",
        borderRadius: "1rem"
    },

    list: {
        width: '100%',
        maxWidth: 360,
        backgroundColor: theme => theme.palette.table,
        position: 'relative',
        overflow: 'auto',
        maxHeight: 300,
    },

    listSection: {
        backgroundColor: 'inherit',
    },

    ul: {
        backgroundColor: 'inherit',
        padding: 0,
    },
}));


function SingleCountry(props) {
    const { currentTheme } = useContext(ThemeContext);
    const classes = useStyles(currentTheme);
    const { name } = useParams();
    const country = useCountry(name);

    const {
        flag,
        nativeName,
        capital,
        altSpellings,
        region, subregion,
        population,
        timezones,
        borders,
        currencies,
        languages
    } = country

    console.log(country)
    const sections =
    {
        "Native name": [nativeName],
        "Capital": [capital],
        "Other names": altSpellings,
        "Region": [region],
        "Subregion": [subregion],
        "Population": [population],
        "Timezones": timezones,
        "Border": borders,
        "Currencies": currencies ? currencies.map(cur => cur.name) : [],
        "Languages": languages ? languages.map(lang => lang.name) : []
    }

    console.log(sections);

    return (
        <div className={classes.root}>
            {Object.keys(country).length !== 0 &&
                <Grid container spacing={5}>
                    <Grid item xs={5}>
                        <Paper className={classes.paper}>
                            <img className={classes.flag} src={flag} alt={`${name}'s flag`} />
                            <h1>{name}</h1>
                        </Paper>
                    </Grid>
                    <Grid item xs={7}>
                        <Paper className={classes.paper}>
                            <List className={classes.list} subheader={<li />}>
                                {Object.keys(sections).map((section) => (
                                    <li key={`section-${section}`} className={classes.listSection}>
                                        <ul className={classes.ul}>
                                            <ListSubheader>{section}</ListSubheader>
                                            {sections[section].map((item, index) => (
                                                <ListItem key={index}>
                                                    <ListItemText primary={item} />
                                                </ListItem>
                                            ))}
                                        </ul>
                                    </li>
                                ))}
                            </List>
                        </Paper>
                    </Grid>
                </Grid>}

        </div >
    )
}

export default SingleCountry;
