import { UPDATE_SEARCH } from '../actions';

const initialState = { input: "" };

const searchReducer = (state = initialState, action) => {
    switch (action.type) {
        case UPDATE_SEARCH:
            return { ...state, input: action.payload.input };
        default:
            return state;
    }
}

export default searchReducer;
