import { TOGGLE_SIDEBAR } from '../actions';

const initialState = { sidebarDisplay: false, like: false };

const toggle = (state = initialState, action) => {
    switch (action.type) {
        case TOGGLE_SIDEBAR:
            return { ...state, sidebarDisplay: !state.sidebarDisplay };
        case TOGGLE_HEART:
            return { ...state, like: !state.like };
        default:
            return state;
    }
}

export default toggle;
