import React, { useState, useContext } from 'react'
import { makeStyles, ThemeProvider } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableContainer from '@material-ui/core/TableContainer';

import Paper from '@material-ui/core/Paper';
import TablePagination from '@material-ui/core/TablePagination';

//import CircularProgress from '@material-ui/core/CircularProgress';

import useCountry from '../../custom-hooks/useCountry';
import CountryTableRow from './CountryTableRow';
import CountryTableHead from './CountryTableHead';
import getComparator from '../../helperFunctions';
import { useDispatch } from 'react-redux';
import { getCountrySuccess } from '../../redux/actions';

//Theme related
import { ThemeContext } from '../../App'

const useStyles = makeStyles({
    root: {
        backgroundColor: theme => theme.palette.background,
        padding: "50px"
    },

    container: {
        borderRadius: "1rem",
        override: "hidden",
        backgroundColor: theme => theme.palette.table,
    },

    table: {
        minWidth: 650,
        backgroundColor: theme => theme.palette.table,
    }
});


function CountryTable(props) {
    const { currentTheme, switchTheme } = useContext(ThemeContext);

    const allCountries = useCountry();
    const dispatch = useDispatch();

    const [page, setPage] = useState(0);
    //Ascending or Descending
    const [order, setOrder] = useState("asc");
    //Order by name, population or region
    const [orderBy, setOrderBy] = useState("name");

    const classes = useStyles(currentTheme);
    console.log(classes);
    const rowsPerPage = 10;

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const sort = () => {
        const sortedCountries = [...allCountries].sort(getComparator(order, orderBy));
        dispatch(getCountrySuccess(sortedCountries));
    }

    return (
        <ThemeProvider theme={currentTheme}>
            <div className={classes.root}>
                <TableContainer className={classes.container} component={Paper}>
                    <Table
                        className={classes.table}
                        aria-label="simple table">

                        <CountryTableHead
                            orderBy={orderBy}
                            setOrderBy={setOrderBy}
                            order={order}
                            setOrder={setOrder}
                            sort={sort} />

                        <TableBody>
                            {allCountries
                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                .map((country) => (
                                    <CountryTableRow key={country.name} country={country} />
                                ))}
                        </TableBody>

                    </Table>
                </TableContainer>

                <TablePagination
                    component="div"
                    count={allCountries.length}
                    page={page}
                    onChangePage={handleChangePage}
                    rowsPerPage={rowsPerPage}
                    rowsPerPageOptions={[5]}
                />
            </div>
        </ThemeProvider>
    )
}


export default CountryTable;
