import React, { useContext } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TableSortLabel from '@material-ui/core/TableSortLabel';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableCell from '@material-ui/core/TableCell';

import CountryTableCell from './CountryTableCell'
import { ThemeContext } from '../../App';

const headCells = [
    { id: 'flag', numeric: false, isSortable: false, label: 'Flag' },
    { id: 'name', numeric: false, isSortable: true, label: 'Name' },
    { id: 'population', numeric: true, isSortable: true, label: 'Population' },
    { id: 'region', numeric: false, isSortable: true, label: 'Region' },
    { id: 'languages', numeric: false, isSortable: false, label: 'Languages' }
];

const useStyles = makeStyles({
    cell: {
        borderBottom: theme => theme.palette.cellborder,
        color: theme => theme.palette.text,
    },
    arrow: {
        color: theme => theme.palette.text,
    }
});

function CountryTableHead({ order, setOrder, orderBy, setOrderBy, sort }) {
    const { currentTheme } = useContext(ThemeContext);
    const classes = useStyles();

    const sortBy = (id) => {
        return async () => {
            if (orderBy !== id) {
                await setOrderBy(id);
            } else {
                await setOrder(order === "asc" ? "desc" : "asc");
            }
            sort();
        }
    }

    return (
        <TableHead className={classes.header}>
            <TableRow>
                {headCells.map(cell => (
                    <TableCell
                        className={classes.cell}
                        key={cell.id}
                        align="center"
                        sortDirection={orderBy === cell.id ? order : false}>
                        <span onClick={cell.isSortable ? sortBy(cell.id) : null}>{cell.label}</span>
                        {
                            cell.isSortable &&
                            <TableSortLabel
                                className={classes.arrow}
                                active={orderBy === cell.id}
                                direction={orderBy === cell.id ? order : 'asc'}>
                            </TableSortLabel>
                        }
                    </TableCell>

                ))}
                <CountryTableCell></CountryTableCell>
            </TableRow>
        </TableHead >
    )
}

export default CountryTableHead;
