// your custom hook goes here
import { useState, useEffect } from "react";

import { useDispatch, useSelector } from 'react-redux';
import { getCountrySaga } from '../redux/actions';

function useCountry(name) {
    const [error, setError] = useState();
    const [isLoading, setIsLoading] = useState(false);
    const dispatch = useDispatch();

    let country;
    if (name) {
        country = useSelector(store => store.countryReducer.country);
    } else {
        country = useSelector(store => store.countryReducer.allCountries);
    }

    useEffect(() => {
        dispatch(getCountrySaga());
    }, [dispatch])
}

export default useCountry;
