import { TOGGLE_SIDEBAR } from '../actions';

const initialState = { open: false };

const toggleReducer = (state = initialState, action) => {
    switch (action.type) {
        case TOGGLE_SIDEBAR:
            return { ...state, open: !state.open };
        default:
            return state;
    }
}

export default toggle;
