// Variables for tgoogle actions
export const TOGGLE_SIDEBAR = "TOGGLE_SIDEBAR";

//Variables for favorite list related actions
export const LIKE = "LIKE";
export const UNLIKE = "DISLIKE";

// Switch the appearance of the sidebar
export const toggleSidebar = () => {
    return {
        type: TOGGLE_SIDEBAR
    }
}

//Add and remove countries from favorite list
export const likeCountry = (country) => {
    return {
        type: LIKE,
        payload: { country }
    }
}

export const unlikeCountry = (countryName) => {
    return {
        type: UNLIKE,
        payload: { countryName }
    }
}

//Action to delever the data from saga to reducer
export const getCountrySuccess = (countryName) => {
    return {
        type: "GET_COUNTRY_SUCCESS",
        payload: { countryName }
    }
}
