import { GET_COUNTRY_SUCCESS, LIKE, UNLIKE, SEARCH, AllActions } from '../actions';
import { Country } from '../../types/types';

type CountryState = {
    favCountries: Country[];
    allCountries: Country[];
    searchCountries: Country[]
    country?: Country;
    input: string
}

const initialState: CountryState =
{
    favCountries: JSON.parse((window as any).localStorage.getItem("favCountries")) || [],
    allCountries: [],
    searchCountries: [],
    input: ""
};

const countryReducer = (state = initialState, action: AllActions): CountryState => {
    switch (action.type) {
        case LIKE:
            return { ...state, favCountries: newFav };

        case UNLIKE:
            const remainingFav = state.favCountries.filter(country => country.name !== action.payload  /*countryName*/);
            localStorage.setItem("favCountries", JSON.stringify(remainingFav));
            return { ...state, favCountries: remainingFav };

        case GET_COUNTRY_SUCCESS:
            const country = action.payload;
            if (country.length === 1) {
                return { ...state, country: country[0] };
            } else {
                return { ...state, allCountries: country, searchCountries: country };
            }

        case SEARCH:
            const input = action.payload;
            const searchResults = state.allCountries.filter(country => country.name.slice(0, input.length).toLowerCase() === input.toLowerCase());
            console.log(searchResults);
            return { ...state, searchCountries: searchResults, input: input }
        default:
            return state;
    }
}

export default countryReducer;
